	VALUES ('tt0021749', 'City Lights', 'With the aid of a wealthy erratic tippler, a dewy-eyed tramp who has fallen in love with a sightless flower girl accumulates money to be able to help her medically.', '[Charles Chaplin, Virginia Cherrill, Florence Lee]', 'Charles Chaplin', '', '1931-03-07', '87 min', 98092, 8.6, 'A tramp falls in love with a beautiful blind girl. Her family is in financial trouble. The tramp''s on-and-off friendship with a wealthy man allows him to be the girl''s benefactor and suitor.', '[http://ia.media-imdb.com/images/G/01/imdb/images/nopicture/small/unknown-1394846836._CB379391227_.png, http://ia.media-imdb.com/images/G/01/imdb/images/nopicture/small/unknown-1394846836._CB379391227_.png, http://ia.media-imdb.com/images/G/01/imdb/images/nopicture/small/unknown-1394846836._CB379391227_.png, http://ia.media-imdb.com/images/G/01/imdb/images/nopicture/small/unknown-1394846836._CB379391227_.png]', '[Comedy, Drama, Romance]', NULL, 'assets/img/tt0021749.jpg', 1931);
INSERT INTO APP.MOVIE (ID, TITLE, DESCRIPTION, STARS, DIRECTOR, CERTIFICATION, RELEASE_DATE, RUNNING_TIME, VOTES, RATING, STORYLINE, GALLERY, GENRE, METASCORE, POSTER, RELEASE_YEAR) 
	VALUES ('tt0054215', 'Psycho', 'A Phoenix secretary steals $40,000 from her employer''s client, goes on the run and checks into a remote motel run by a young man under the domination of his mother.', '[Anthony Perkins, Janet Leigh, Vera Miles]', 'Alfred Hitchcock', '', '1960-09-08', '109 min', 389986, 8.5, 'Phoenix officeworker Marion Crane is fed up with the way life has treated her. She has to meet her lover Sam in lunch breaks and they cannot get married because Sam has to give most of his money away in alimony. One Friday Marion is trusted to bank $40,000 by her employer. Seeing the opportunity to take the money and start a new life, Marion leaves town and heads towards Sam''s California store. Tired after the long drive and caught in a storm, she gets off the main highway and pulls into The Bates Motel. The motel is managed by a quiet young man called Norman who seems to be dominated by his mother.', '[http://ia.media-imdb.com/images/G/01/imdb/images/nopicture/small/unknown-1394846836._CB379391227_.png]', '[Horror, Mystery, Thriller]', NULL, 'assets/img/tt0054215.jpg', 1960);
INSERT INTO APP.MOVIE (ID, TITLE, DESCRIPTION, STARS, DIRECTOR, CERTIFICATION, RELEASE_DATE, RUNNING_TIME, VOTES, RATING, STORYLINE, GALLERY, GENRE, METASCORE, POSTER, RELEASE_YEAR) 
	VALUES ('tt0082971', 'Raiders of the Lost Ark', 'Archaeologist and adventurer Indiana Jones is hired by the US government to find the Ark of the Covenant before the Nazis.', '[Harrison Ford, Karen Allen, Paul Freeman]', 'Steven Spielberg', '', '1981-06-12', '115 min', 611296, 8.5, 'The year is 1936. An archeology professor named Indiana Jones is venturing in the jungles of South America searching for a golden statue. Unfortunately, he sets off a deadly trap but miraculously escapes. Then, Jones hears from a museum curator named Marcus Brody about a biblical artifact called The Ark of the Covenant, which can hold the key to humanly existence. Jones has to venture to vast places such as Nepal and Egypt to find this artifact. However, he will have to fight his enemy Rene Belloq and a band of Nazis in order to reach it.', '[http://ia.media-imdb.com/images/G/01/imdb/images/nopicture/small/unknown-1394846836._CB379391227_.png, http://ia.media-imdb.com/images/G/01/imdb/images/nopicture/small/unknown-1394846836._CB379391227_.png, http://ia.media-imdb.com/images/G/01/imdb/images/nopicture/small/unknown-1394846836._CB379391227_.png]', '[Action, Adventure]', 90.0, 'assets/img/tt0082971.jpg', 1981);
